$(document.body).ready(function(){
    $("#content_2").show();

    var margin = {top: 40, right: 10, bottom: 10, left: 10},
        width =$(".content_bar").width(),
        height =$(document).height()-$(".menu").height()-100;

    var div =$(".content").find(".content_bar");

    //1.显示内容切换菜单事件绑定
    $(".menu_buttonitem").bind("click",function(ev){
        $(this).parent().children().removeClass("menu_button_selected");
        $(this).addClass("menu_button_selected");

        $(".content").hide();
        $("#"+$(this).data("itemvalue")).show();
        if($(this).data("itemvalue")=="content_2"){
            ChainTypeChanged();
        }
    })

    //日期选择
    var paneltemp=$("#datepicker01").Zebra_DatePicker({
        format:"Y/m/d",
        //format:"Y-m",
        direction: ["2014/01/01", "2014/01/11"],
        onSelect:function(date){
            FilterDateValue=date;
            LoadChainTable(FilterDataByDate(TreeMapData,FilterDateValue));//进度完成分析
            RefreshMap();
        },
        onClear:function(){
            FilterDateValue="2014/01/01";
            RefreshMap();
        }
    }).val("2014/01/01");

    LoadTreeMap();//现金流总览
});

