function FilterConditionChanged(){
    NumberRangeMin=$("#numberrange1").data("rangevalue");

    var zdevicechecked=$("#content_1").find(".checkboxitem[value='正现金流装置']").attr("checked");
    var fdevicechecked=$("#content_1").find(".checkboxitem[value='负现金流装置']").attr("checked");

    var valuefunc =null;
    if(zdevicechecked=="checked" && fdevicechecked=="checked"){
        DataFilterfunc =function(d) {
            if(d.size>=NumberRangeMin){
                return Math.abs(d.size);
            }
        };
    }else{
        if(fdevicechecked=="checked"){
            DataFilterfunc =function(d) {
                if(d.size>=NumberRangeMin) {
                    if (d.size < 0) {
                        return Math.abs(d.size);
                    } else {
                        return 0;
                    }
                }
            };
        }else{
            DataFilterfunc =function(d) {
                if(d.size>=NumberRangeMin) {
                    if (d.size >= 0) {
                        return d.size;
                    } else {
                        return 0;
                    }
                }
            };
        }
    }
    TreeMapNode.data(treemap.value(DataFilterfunc).nodes)
        .transition()
        .style("background", function(d) {
            //return d.children ? color(d.name) : null;
            if(d.size>=0){return "#48c09a"}else{return "#ec5c5c"}
        })
        .duration(1500)
        .call(position);
}
function dataConvert(_dataset,_ColName,_datevalue){
    var convertdata={
        treedata:{
            "name": "root",
            "children": []
        },
        minmaxinfo:{
            min:null,
            max:null
        }
    }

    var temprootnames=[];
    var tempindex=-1;
    for(var i=0;i<_dataset.length;i++){
        if(_dataset[i]["日期"]===_datevalue){
            tempindex=temprootnames.indexOf(_dataset[i]["公司名称"]);
            if(tempindex>-1){
                convertdata.treedata.children[tempindex].children.push({name:_dataset[i]["名称"],size: parseFloat(_dataset[i][_ColName])});
            }else{
                temprootnames.push(_dataset[i]["公司名称"]);
                convertdata.treedata.children.push({name:_dataset[i]["公司名称"],children:[{name:_dataset[i]["名称"],size: parseFloat(_dataset[i][_ColName])}]});
            }

            if(convertdata.minmaxinfo.min==null){
                convertdata.minmaxinfo.min=parseFloat(_dataset[i][_ColName]);
            }else{
                if(parseFloat(_dataset[i][_ColName])<convertdata.minmaxinfo.min){
                    convertdata.minmaxinfo.min=parseFloat(_dataset[i][_ColName]);
                }
            }
            if(convertdata.minmaxinfo.max==null){
                convertdata.minmaxinfo.max=parseFloat(_dataset[i][_ColName]);
            }else{
                if(parseFloat(_dataset[i][_ColName])>convertdata.minmaxinfo.max){
                    convertdata.minmaxinfo.max=parseFloat(_dataset[i][_ColName]);
                }
            }
        }
    }
    //最小值替换
    return convertdata;
}
function position() {
    this.style("left", function(d) { return d.x + "px"; })
        .style("top", function(d) { return d.y + "px"; })
        .style("width", function(d) { return Math.max(0, d.dx - 1) + "px"; })
        .style("height", function(d) { return Math.max(0, d.dy - 1) + "px"; });
}
function FilterDataByDate(_Data,_Date){
    var tempData=[];
    for(var i=0;i<_Data.length;i++){
        if(_Data[i]["日期"]===_Date){
            tempData.push(_Data[i]);
        }
    }
    return tempData;
}
